mapper.select:
select r.*, a.aritcle_title, u.user_name reply_user_name, u1.user_name replied_user_name
from reply r inner join article a on r.article_id = a.article_id inner join user u on r.reply_user_id = u.user_id inner join user u1 on r.replied_user_id = u1.user_id
where r.readed = 0 and r.replied_user_id = #{id}
